import React, { Component } from 'react';
import ButtonTrend from './ButtonTrend.js'
import styles from './Container.module.css';
import axios from 'axios';
import Datas from './Api/Api-request.js'

class Container extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title:[],
      reponse:[],
      value:"FR",
      display:"none"
    }
  }

  componentDidMount(){
  this.News();
  Datas()
  }


  async News(){
    const response = await fetch('/news-trend-'+this.state.value);
    const body = await response.json();
      this.setState({reponse:body});
  }

  handleChange (event){
     this.setState({value: event.target.value}, ()=>{
       this.News();
     });

 }

 handleClick(){

  if (this.state.display=="none"){
    this.setState({display:"inherit"});
  }else{
    this.setState({display:"none"});
  }
}

  render(props) {

    return (
      <div className="Container">
      <select id="cars" name="cars" onChange={this.handleChange.bind(this)} value={this.state.value}>
         <option value="FR">France</option>
         <option value="US">Etats-Unis</option>
         <option value="DE">Allemagne</option>
       </select>

      {
        this.state.reponse.map((trend, index) =>
      <div>
       <ButtonTrend click={this.handleClick.bind(this)} key={index} title={trend.topic}/>
        <div className={styles.articles} style={{display:this.state.display}}>
        {trend.articles==undefined ? console.log("nkj")
        : trend.articles.length==0? console.log(trend.articles.length):
        trend.articles.map((article, index) => {
        return (
          <div key={index} className="styles.article">
          <a className={styles.articleLink} href={article.url}>{article.title}</a>
          </div>
        );
      })
        }
      </div>
       </div>
     )}




      </div>
    );
  }
}

export default Container;
