import React, { Component } from 'react';
import ButtonTrend from './ButtonTrend.js'
import styles from './ButtonTrend.module.css';
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title:[],
      US:[{country:'US',topic:"hojh"},{country:'US',topic:"hojh"}],
      fr:[{country:'US',topic:"hojh"},{country:'US',topic:"hojh"}],
      de:[{country:'US',topic:"hojh"},{country:'US',topic:"hojh"}],
      value:""
    }
  }

  componentDidMount(){
  this.News();

  }


  async News(){
    const response = await fetch('/news-trend-FR');
    const body = await response.json();
    this.setState({fr:body});
  }

  change (event){
     this.setState({value: event.target.value}, ()=>{
       console.log(this.state.value)
     });
 }

  render(props) {
    return (
      <div className="Container">
      {this.state.fr.map((trend, index) =>

       <ButtonTrend key={index} title={trend.topic}/>
     )}

     <select id="cars" name="cars" onChange={this.change.bind(this)} value={this.state.value}>
        <option value="volvo">Volvo</option>
        <option value="saab">Saab</option>
        <option value="fiat">Fiat</option>
        <option value="audi">Audi</option>
      </select>
      </div>
    );
  }
}

export default App;
