import React, { Component } from 'react';
import ButtonTrend from './ButtonTrend.js'
import styles from './ButtonTrend.module.css';
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title:[],
      US:[{country:'US',topic:"hojh"},{country:'US',topic:"hojh"}],
      FR:[{country:'US',topic:"hojh"},{country:'US',topic:"hojh"}],
      DE:[{country:'US',topic:"hojh"},{country:'US',topic:"hojh"}],
      value:"FR"
    }
  }

  componentDidMount(){
  this.News();

  }


  async News(){
    const response = await fetch('/news-trend-'+this.state.value);
    const body = await response.json();
    if (this.state.value=='FR'){
      this.setState({FR:body});
    }
    else if (this.state.value=='US'){
      this.setState({US:body});
    }else{
      this.setState({DE:body});
    }
  }

  change (event){
     this.setState({value: event.target.value}, ()=>{
       console.log(this.state.value)
     });
 }

  render(props) {
    return (
      <div className="Container">
    
     <select id="cars" name="cars" onChange={this.change.bind(this)} value={this.state.value}>
        <option value="FR">France</option>
        <option value="US">Etats-Unis</option>
        <option value="DE">Allemagne</option>
      </select>
      </div>
    );
  }
}

export default App;
