import React, { Component } from 'react';
import ButtonTrend from './ButtonTrend.js'
import styles from './ButtonTrend.module.css';
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title:[],
      US:[{country:'US',topic:"hojh"}],
      fr:[],
      de:[],
      reponse:[{country:'US',topic:"hojh"},{country:'US',topic:"hojh"}]
    }
  }

  componentDidMount(){
  this.Newspaper();

  }


  async Newspaper(){
    const response = await fetch('/news-trend');
    const body = await response.json();
    console.log(body)
    this.setState({reponse:body});
    this.Select();
  }

  Select(){
    this.state.reponse.map((function(num){
      if (num.country=='US'){
        console.log(num)

      }
    }))
  }

  render(props) {
    return (
      <div className="Container">
      {this.state.reponse.map((trend, index) =>

       <ButtonTrend key={index} title={trend.topic}/>
     )}


      </div>
    );
  }
}

export default App;
