import React, { Component } from 'react';
import ButtonTrend from './ButtonTrend.js'
import styles from './ButtonTrend.module.css';
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title:[],
      US:[{country:'US',topic:"hojh"},{country:'US',topic:"hojh"}],
      FR:[{country:'US',topic:"hojh"},{country:'US',topic:"hojh"}],
      DE:[{country:'US',topic:"hojh"},{country:'US',topic:"hojh"}],
      reponse:[{country:'US',topic:"hojh"},{country:'US',topic:"hojh"}],
      value:"FR"
    }
  }

  componentDidMount(){
  this.News();

  }


  async News(){
    const response = await fetch('/news-trend-'+this.state.value);
    const body = await response.json();
    console.log(body)
      this.setState({reponse:body});
  }

  handleChange (event){
     this.setState({value: event.target.value}, ()=>{
       console.log(this.state.value)
     });
     this.News();
 }

  render(props) {
    return (
      <div className="Container">
      {
        this.state.reponse.map((trend, index) =>

       <ButtonTrend key={index} title={trend.topic}/>
     )}

     <select id="cars" name="cars" onChange={this.handleChange.bind(this)} value={this.state.value}>
        <option value="FR">France</option>
        <option value="US">Etats-Unis</option>
        <option value="DE">Allemagne</option>
      </select>
      </div>
    );
  }
}

export default App;
