import wiki from 'wikijs'

 const wikiped = (data)=>{

   const wikiSearchReturnValues = []

     var url = "https://fr.wikipedia.org/w/api.php";

     var params = {
       action: 'query',
       list: 'search',
       srsearch: data,
       format: 'json'
     };

     url = url + '?origin=*';
     Object.keys(params).forEach((key) => {
       url += "&" + key + "=" + params[key];
     });

     fetch(url)
       .then(
         function (response) {
           return response.json();
         }
       )
       .then(
         function (response) {


           for (var key in response.query.search) {
            wikiSearchReturnValues.push({
               queryResultPageFullURL: 'no link',
               queryResultPageID: response.query.search[key].pageid,
               queryResultPageTitle: response.query.search[key].title,
               queryResultPageSnippet: response.query.search[key].snippet
             });
           }
         }
       )
       .then(
         function (response) {
           console.log(wikiSearchReturnValues[0])
            if (wikiSearchReturnValues[0] != []){
             let page = wikiSearchReturnValues[0];
             let pageID = page.queryResultPageID;
             let urlForRetrievingPageURLByPageID = `https://en.wikipedia.org/w/api.php?origin=*&action=query&prop=info&pageids=${pageID}&inprop=url&format=json`;
             const op = wikiSearchReturnValues[0].queryResultPageTitle;
             console.log(op)
             function jo(){
               console.log(op)
               wiki({  })

               .find(op)
               .then(page => console.log(page.title))

             }
             jo()

             fetch(urlForRetrievingPageURLByPageID)
               .then(
                 function (response) {

                   return response.json();
                 }
               )
               .then(
                 function (response) {
                   page.queryResultPageFullURL = response.query.pages[pageID].fullurl;
                  console.log( page.queryResultPageFullURL)

                 }
               )
           }else {
             console.log("nop)")
           }
         }
       )

   }




export default wikiped
