import React, { Component } from 'react';
import ButtonTrend from './ButtonTrend.js'
import styles from './Container.module.css';
import axios from 'axios';
import Datas from './Api/Api-request.js'
import wiki from 'wikijs'

class Container extends Component {
  constructor(props) {
    super(props);
    this.state = {
      reponse:[],
      reponseFR:[],
      reponseUS:[],
      reponseDE:[],
      wiki:"Sam",
      value:"FR",
      display:"none",
      googleSearch:"Sam",
      twitterSearch:"Sam,"
    }
  }

  componentDidMount(){
    this.News()
  }


  async News(){
    const responseFR = await fetch('/news-trend-FR');
    const responseUS = await fetch('/news-trend-US');
    const responseDE = await fetch('/news-trend-DE');
    const bodyFR = await responseFR.json();
    const bodyUS = await responseUS.json();
    const bodyDE = await responseDE.json();
    console.log(bodyFR)
    if (this.state.reponse.length == 0){this.setState( {reponse:bodyFR})} else {console.log("kjb")}
    this.setState( {reponseUS:bodyUS})
    this.setState( {reponseFR:bodyFR})
    this.setState( {reponseDE:bodyDE})
  }

  handleChange (event){
    this.setState({value: event.target.value}, ()=>{
      console.log(this.state.value)
    })


  }

  handleClick(e){
    if (this.state.display=="none"){
      this.setState({display:"inherit"});
    }else{
      this.setState({display:"none"});
    }
    const elem = e.target.innerHTML
    const id = e.target.id;
    this.wikipedia(elem);
    this.googleSearch(elem);
    this.twitterSearch(elem);
     const element = document.getElementById(id)
    element.className=styles.articleContainerFull
  //  elemtn.classList.remove("styles.articleContainerFull")
  }

  wikipedia (data){
    wiki({ apiUrl: 'https://fr.wikipedia.org/w/api.php' })
    .find(data)
    .then(page => this.setState({wiki:page.raw.fullurl} ))
  }

  googleSearch(data){
    const search = 'https://www.google.com/search?q='+data;
    this.setState({googleSearch:search})
  }

  twitterSearch(data){
    const search = 'https://twitter.com/search?q='+data+'&src=typed_query'
    this.setState({twitterSearch:search})
  }

  render(props) {

    return (
      <div className={styles.Container}>
      <div className={styles.selectContainer}>
      <select id="cars" name="cars" className={styles.selector} onChange={this.handleChange.bind(this)} value={this.state.value}>
      <option className={styles.options} value="FR">France</option>
      <option className={styles.options} value="US">Etats-Unis</option>
      <option className={styles.options} value="DE">Allemagne</option>
      </select>
      </div>
      <div className={styles.dataConatainer}>
      {
        this.state.reponse.map((trend, index) =>
        <div id={index} className={styles.articleContainer}>

        <ButtonTrend id={index} click={this.handleClick.bind(this)} key={index} title={trend.topic}/>

        <div className={styles.articles} style={{display:this.state.display}}>
         <h1><a href={this.state.wiki}>{this.state.wiki}</a></h1>
         <h1><a href={this.state.googleSearch}>{this.state.googleSearch}</a></h1>
         <h1><a href={this.state.twitterSearch}>{this.state.twitterSearch}</a></h1>
        {trend.articles==undefined ?
          console.log("article-undefined")
          : trend.articles.length==0?
          console.log(trend.articles.length):
          trend.articles.map((article, index) => {
            return (
              <div key={index} className="styles.article">
              <a className={styles.articleLink} href={article.url}>{article.title}</a>
              </div>
            );
          })
        }


        </div>
        </div>
      )}
      <div className={styles.outro}></div>
      </div>



      </div>
    );
  }
}

export default Container;
